<div class="global-container">
	<div class="card login-form">
	<div class="card-body">
		<h3 class="card-title text-center">Log in</h3>
		<div class="card-text"><form>
				<div class="form-group">
					<label for="exampleInputEmail1">Usuario</label>
					<input  type="email" class="form-control form-control-sm" id="user" aria-describedby="emailHelp">
				</div>
				<div class="form-group">
					<label for="exampleInputPassword1">Password</label>
					<input  type="password" class="form-control form-control-sm" id="clave">
				</div>
				<button  (click)="IniciarSesion()" type="submit" class="btn btn-primary btn-block"
                >Sign in</button>
			</form>     

		</div>
	</div>
</div>
</div>
